/* eslint-disable no-continue */
/* eslint-disable no-restricted-syntax */
import React from "react";

import Head from "next/head";
import dynamic from "next/dynamic";
import { get, cloneDeep } from "lodash";

import { gsap } from "gsap/dist/gsap";

import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

import { ScrollToPlugin } from "gsap/dist/ScrollToPlugin";

const LoadingPageComponent = dynamic(
  () => import("../../src/Components/PageLoading/index"),
  {
    ssr: false,
  }
);

const PageBotComponent = dynamic(
  () => import("../../src/Components/PageBot/index"),
  {
    ssr: false,
  }
);

const Page1Desktop = dynamic(
  () => import("../../src/Components/Desktop/Page1"),
  {
    ssr: false,
  }
);

const Page2Desktop = dynamic(
  () => import("../../src/Components/Desktop/Page2"),
  {
    ssr: false,
  }
);

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageHeight: 0,
      pageWidth: 0,
      loading: true,
    };
    this._refElement = {
      loading: null,
      phone: null,
    };
    this.pageWaiting = 2500;
    this.refTrigger = null;

    this._isBot = false;

    if (props.isBot === true) {
      this._isBot = true;
      this.state.loading = false;
    } else {
      gsap.registerPlugin(ScrollTrigger, ScrollToPlugin);
    }
  }

  componentDidMount() {
    if (this._isBot === false) {
      gsap.to(window, { scrollTo: 0 });
      const posY = window.scrollY;

      if (posY === 0) {
        requestAnimationFrame(() => {
          try {
            this.setState({
              pageHeight: window.innerHeight,
              pageWidth: window.innerWidth,
            });

            setTimeout(() => {
              this.setState({
                loading: false,
              });

              gsap.to(window, {
                scrollTo: window.innerHeight / 2 + 1000,
                duration: 1.5,
              });
            }, this.pageWaiting);
          } catch (err) {
            //
          }
        });
      }
      window.addEventListener("resize", this._handleResize);
      window.addEventListener("scroll", this._handleScroll);
    }
  }

  componentWillUnmount() {
    if (this._isBot === false) {
      window.removeEventListener("resize", this._handleResize);
      window.removeEventListener("scroll", this._handleScroll);
    }
  }

  _handleScroll = () => {
    const posY = window.scrollY;
    // console.log(posY);
    if (posY === 0 && this.state.loading !== false) {
      setTimeout(() => {
        this.setState({
          loading: false,
        });

        gsap.to(window, {
          scrollTo: window.innerHeight / 2 + 1000,
          duration: 1.5,
        });
      }, this.pageWaiting);
    }
  };

  _handleResize = () => {
    this.setState({
      pageWidth: window.innerWidth,
      pageHeight: window.innerHeight,
    });
  };

  _renderLoadingPage = () => {
    return (
      <section id="loading" className="sticky loading" style={{ zIndex: 1 }}>
        <div
          className="content"
          style={{
            height: "100vh",
            overflow: "hidden",
          }}
        >
          <LoadingPageComponent />;
        </div>
      </section>
    );
  };

  _renderMainPage = () => {
    const { pageWidth, pageHeight } = this.state;

    return (
      <section
        id="loading"
        className="sticky loading"
        style={{ zIndex: 1 }}
        ref={ele => {
          if (ele) {
            this._refElement.loading = ele;
          }
        }}
      >
        <div
          className="content"
          style={{
            height: "5000vh",
            overflow: "hidden",
          }}
        >
          <Page1Desktop/>
          <Page2Desktop/>
        </div>
      </section>
    );
  };

  _renderBotPage = () => {
    return <PageBotComponent />;
  };

  render() {
    const { loading } = this.state;
    console.log("loading", loading);
    console.log("is_bot", this._isBot);
    let layoutHtml = this._renderLoadingPage();

    if (loading === false && this._isBot === false) {
      layoutHtml = this._renderMainPage();
    }

    if (this._isBot === true) {
      layoutHtml = this._renderBotPage();
    }

    return (
      <div>
        <Head>
          <title>Ibenefit Nhận Ưu Đãi</title>
          <link rel="icon" href="/favicon.ico" />
          <meta
            name="viewport"
            content="width=device-width, initial-scale=1.0, 
            maximum-scale=1.0, user-scalable=no, shrink-to-fit=no"
           />
           <meta name="HandheldFriendly" content="true" />
        </Head>
        {layoutHtml}
      </div>
    );
  }
}

// This gets called on every request
export async function getServerSideProps(context) {
  const { req } = context;
  const userAgent = req.headers["user-agent"] || "";

  const arrBot = [
    "bot",
    "google",
    "facebook",
    "twitter",
    "linked",
    "speed",
    "test",
    "search",
    "craw",
  ];
  let isBot = false;
  for (const i in arrBot) {
    if (userAgent.indexOf(arrBot[i]) < 0) {
      continue;
    }
    isBot = true;
    break;
  }
  // #debug
  // isBot = true;
  if (isBot === false) {
    // logic when run time is not bot
    return { props: { isBot: false } };
  }

  // Pass data to the page via props
  return { props: { isBot: true } };
}

export default Home;
